import React, { useState } from 'react';

function TimeSelector() {
  const [selectedOption, setSelectedOption] = useState('');

  const handleButtonClick = (option) => {
    setSelectedOption(option);
  };

  return (
    <div className="m-10 w-auto justify-between rounded-lg border-2 border-orange-500">
            <button
              className={`bg-transparent py-1 text-[28px] ${selectedOption === 'tcas1' ? 'bg-orange-500 text-white' : 'hover:bg-orange-500 hover:text-white'}`}
              onClick={() => handleButtonClick('tcas1')}
            >
              รอบ 1 Portfolio
            </button>
            <button
              className={`bg-transparent py-1 text-[28px] ${selectedOption === 'tcas2' ? 'bg-orange-500 text-white' : 'hover:bg-orange-500 hover:text-white'}`}
              onClick={() => handleButtonClick('tcas2')}
            >
              รอบ 2 Quota
            </button>
            <button
              className={`bg-transparent py-1 text-[28px] ${selectedOption === 'tcas3' ? 'bg-orange-500 text-white' : 'hover:bg-orange-500 hover:text-white'}`}
              onClick={() => handleButtonClick('tcas3')}
            >
              รอบ 3 Admission
            </button>
          </div>
    // <div>
    //   <button onClick={() => handleOptionClick('9-10')} style={{ background: selectedOption === '9-10' ? 'red' : 'transparent' }}> 9.00 น. - 10.00 น. </button>
    //   <button onClick={() => handleOptionClick('11-12')} style={{ background: selectedOption === '11-12' ? 'red' : 'transparent' }}> 11.00 น. - 12.00 น. </button>
    //   <button onClick={() => handleOptionClick('17-18')} style={{ background: selectedOption === '17-18' ? 'red' : 'transparent' }}> 17.00 น. - 18.00 น. </button>
    //   <button onClick={() => handleOptionClick('19-20')} style={{ background: selectedOption === '19-20' ? 'red' : 'transparent' }}> 19.00 น. - 20.00 น. </button>
    // </div>
  );
}

export default TimeSelector;
