import React, { useState } from 'react';

function TimeSelector() {
  const [selectedOption, setSelectedOption] = useState(null);
  const [activeButton, setActiveButton] = useState('');

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  return (
    <div>
      <button
              className={`bg-transparent py-1 text-[28px] ${activeButton === 'tcas1' ? 'bg-orange-500 text-white' : 'hover:bg-orange-500 hover:text-white'}`}
              onClick={() => handleOptionClick('tcas1')}
            >
              รอบ 1 Portfolio
            </button>
      <button className="bg-orange-400 rounded-9" onClick={() => handleOptionClick('9-10')} style={{ background: selectedOption === '9-10' ? 'red' : 'transparent' }}> 9.00 น. - 10.00 น. </button>
      <button className="bg-orange-400 rounded-9" onClick={() => handleOptionClick('11-12')} style={{ background: selectedOption === '11-12' ? 'red' : 'transparent' }}> 11.00 น. - 12.00 น. </button>
      <button className="bg-orange-400 rounded-9" onClick={() => handleOptionClick('17-18')} style={{ background: selectedOption === '17-18' ? 'red' : 'transparent' }}> 17.00 น. - 18.00 น. </button>
      <button className="bg-orange-400 rounded-9" onClick={() => handleOptionClick('19-20')} style={{ background: selectedOption === '19-20' ? 'red' : 'transparent' }}> 19.00 น. - 20.00 น. </button>
    </div>
  );
}

export default TimeSelector;
