import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [user, setUser] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    if (user === "qwerty" && password === "qwerty") {
        navigate("/Home");
    } else {
        alert("Login fail");
    }
  };

  return (
    <div className="flex justify-center items-center h-screen">
        <div className="w-1/3 h-2/3 bg-white shadow-lg rounded-lg flex justify-center items-center">
            <div className="w-2/3 h-2/3">
                <h2 className="text-3xl my-2.5">ยินดีต้อนรับ</h2>
                <h1 className="text-5xl leading-6 my-2.5">เข้าสู่ระบบ</h1>
                <form onSubmit={handleLogin}>
                    <div className="my-5">
                        <p className="text-3x">Username</p>
                        <input 
                            className="w-full bg-white shadow-lg rounded-lg outline-none border border-gray-300 p-4"
                            type="text"
                            value={username}
                            onChange={(e) => setUser(e.target.value)}
                            required
                        />
                    </div>
                    <div className="my-5">
                        <p className="text-3x">Password</p>
                        <input
                            className="w-full bg-white shadow-lg rounded-lg outline-none border border-gray-300 p-4"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit">เข้าสู่ระบบ</button>
                </form>
            </div>
        </div>
    </div>
  );
};

export default Login;
